/*-------------------------------------------*/
var InterValObj; //timer变量，控制时间
var count = 120; //间隔函数，1秒执行
var curCount;//当前剩余秒数
var code = ""; //验证码
var codeLength = 6;//验证码长度
$(document).ready(function () {

    $("#btnSendCode").click(function () {
        curCount = count;
        //产生验证码
        for (var i = 0; i < codeLength; i++) {
            code += parseInt(Math.random() * 9).toString();
        }

        //设置button效果，开始计时
        $("#st").attr("disabled", true);
        $("#st").attr("disabled", true);
        $("#xxphone").attr("disabled", true);
        $("#btnSendCode").val(curCount + "秒后可重发"); 
        InterValObj = window.setInterval(SetRemainTime, 1000); //启动计时器，1秒执行一次
        //向后台发送处理数据  
       $.ajax({
            type: 'POST',
            url: "/Buy/Qcode",//你的action
            data: {
                val1: $("#xxphone").val(),
                val2: code
            },
            function(response, status, xhr) {
                alert(response);
            }
        });
    });
});


//timer处理函数
function SetRemainTime() {
    if (curCount == 0) {
        window.clearInterval(InterValObj);//停止计时器
        $("#btnSendCode").removeAttr("disabled");//启用按钮
        $("#btnSendCode").val("重新发送验证码");
        code = ""; //清除验证码。如果不清除，过时间后，输入收到的验证码依然有效    
    }
    else {
        curCount--;
        $("#btnSendCode").val(curCount + "秒后可重发");
    }
}




//非IE浏览器
function OnInput(event) {
    if (event.target.value.length == 6) {
        if ($.trim(event.target.value) == $.trim(code)) {
           
            $("#btnSendCode").attr("disabled", true);
            window.clearInterval(InterValObj);
            $("#btnSendCode").val("已成功验证");
            $("#qcode").attr("disabled", true);
            $("#xxphone").attr("disabled", true);
            $("#st").attr("disabled", false);
          
        }
    }
}
// Internet Explorer
function OnPropChanged(event) {

    if (event.propertyName.toLowerCase() == "value") {
        if (event.srcElement.value.length == 6) {
            if ($.trim(event.srcElement.value) == $.trim(code))
            {
                $("#btnSendCode").attr("disabled", true);
                window.clearInterval(InterValObj);
                $("#btnSendCode").val("已成功验证"); 
                $("#qcode").attr("disabled", true);
                $("#xxphone").attr("disabled", true);
                $("#st").attr("disabled", false);
            }
        }
    }
}








